import random

from Crypto.Util.number import *
flag = b'*****'
k = getPrime(512)
p = k + random.randint(0,2**200)
while isPrime(p)==0:
    p = k + random.randint(0,2**200)
q = k + random.randint(0,2**200)
while isPrime(q)==0:
    q = k + random.randint(0,2**200)
n = p*q
e = 65537
m = bytes_to_long(flag)
c = pow(m,e,n)
print('n = ',n)
print('c = ',c)
